SELECT country.name AS name_of_country, city.name AS name_of_capital, city.population FROM country JOIN city ON country.capital = city.id;
SELECT country.continent AS name_of_continent, COUNT(DISTINCT countrylanguage.language) AS number_of_language FROM country JOIN countrylanguage ON country.code = countrylanguage.countrycode WHERE countrylanguage.isofficial = 'T' GROUP BY country.continent;
SELECT country.GNP AS GNP_of_the_country, city.name AS name_of_capital FROM country JOIN city ON country.capital = city.id;
SELECT city.name AS name_of_cities, city.population AS population_of_city FROM country JOIN city ON country.code = city.countrycode WHERE city.population > (SELECT AVG(city.Population) FROM City WHERE country.Code = city.CountryCode);
SELECT country.name AS name_of_country, countrylanguage.language, countrylanguage.percentage FROM country JOIN countrylanguage ON country.code = countrylanguage.countrycode WHERE countrylanguage.percentage = (SELECT MAX(countrylanguage.percentage) FROM countrylanguage WHERE countrylanguage.countrycode = country.code);
SELECT country.continent, country.name, city.name AS name_of_the_capital FROM country JOIN city ON country.capital = city.id;
SELECT DISTINCT country.name FROM country JOIN city ON country.code = city.countrycode WHERE city.population > 5000000;
SELECT city.name AS name_of_cities, country.lifeexpectancy AS life_expectancy_in_the_country FROM city JOIN country ON country.code = city.countrycode WHERE country.continent = "Africa";
SELECT country.name, COUNT(DISTINCT countrylanguage.language) AS number_of_language FROM country JOIN countrylanguage ON country.code = countrylanguage.countrycode GROUP BY country.name;
SELECT country.continent, country.region, city.name AS name_of_capital FROM country JOIN city ON country.capital = city.id;
SELECT country.continent, country.name AS name_of_cities , city.population FROM country JOIN city ON country.code = city.countrycode ORDER BY city.population DESC LIMIT 10;
SELECT country.region, country.name, COUNT(city.id) AS number_of_cities FROM country JOIN city ON country.code = city.countrycode GROUP BY country.region, country.name;
SELECT country.name, city.name AS name_of_the_capital FROM country JOIN city ON country.capital = city.id Join countrylanguage ON country.code = countrylanguage.countrycode WHERE countrylanguage.language = "Spanish" AND countrylanguage.isofficial = "T";
SELECT countrylanguage.language, SUM(country.population) AS population_of_country FROM country JOIN countrylanguage ON country.code = countrylanguage.countrycode WHERE countrylanguage.isofficial = "T" GROUP BY countrylanguage.language;
SELECT country.name, city.name AS name_of_the_capital FROM country JOIN city ON country.capital = city.id WHERE country.continent = "Asia" ORDER BY city.population DESC;
SELECT country.name, city.name AS name_of_the_capital, city.district FROM country JOIN city ON country.capital = city.id;
SELECT country.region, SUM(country.population) AS total_population FROM country GROUP BY country.region;
SELECT country.continent, city.name  AS name_of_the_capital, city.population FROM country JOIN city ON city.id = country.capital JOIN (SELECT country.continent, MAX(city.population) AS max_population FROM country JOIN city ON city.id = country.capital GROUP BY country.continent) sub ON country.continent = sub.continent AND city.population = sub.max_population ORDER BY country.continent;
SELECT country.name, city.name AS name_of_the_capital, COUNT(countrylanguage.language) AS number_of_language FROM country JOIN city ON country.capital = city.id JOIN countrylanguage ON countrylanguage.countrycode = country.code GROUP BY country.name, city.name;
SELECT country.name AS official_french_language FROM country JOIN countrylanguage ON country.code = countrylanguage.countrycode WHERE countrylanguage.language = "French" AND countrylanguage.isofficial = "T";